import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { CharacterService } from '../services/character.service'; // Import your character creation service


@Component({
  selector: 'app-create-character-modal',
  templateUrl: './create-character-modal.component.html',
  styleUrls: ['./create-character-modal.component.css']
})
export class CreateCharacterModalComponent {
  characterForm: FormGroup;

  constructor(private characterService: CharacterService) {}

  onSubmit(): void {
    if (this.characterForm.valid) {
      this.characterService.createCharacter(this.characterForm.value).subscribe(
        response => {
          // Handle response
          console.log(response);
          this.close(); // Close the modal
        },
        error => {
          // Handle error
          console.error('Error creating character:', error);
        }
      );
    }
  }

  close(): void {
    // Logic to close the modal
  }
}
