import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { RegistrationResponse } from '../DTOs/RegistrationResponse';
import { Router } from 'express';

@Injectable({
  providedIn: 'root'
})
export class AuthenticationService {
  private token: any;
  private headers: String = "";

  constructor(private http: HttpClient,
    private router: Router,
    ) {
   }

  login(username: String, password: String) {
    const payload = {
      username: username,
      password: password
    };

    this.http.post<RegistrationResponse>('http://localhost:8080/login', payload, { responseType: 'json' }).subscribe(response => {
      console.log('Login successful!', response);
      this.setToken(response.token);
    }, error => {
      console.error('Login failed!', error);
    });
  }


  register(username: String, password: String) {
    const payload = {
      username: username,
      password: password
    };

    this.http.post<RegistrationResponse>('http://localhost:8080/register', payload, { responseType: 'json' }).subscribe(response => {
      console.log('Registration successful!', response);
      this.setToken(response.token);
    }, error => {
      console.error('Registration failed!', error);
    });
  }

  public getToken() {
    return localStorage.getItem('token');
  }

  public isLoggedIn() {
    return this.getToken() !== ""
  }

  public logout() {
    this.setToken("");
  }

  private setToken(token: any) {
    localStorage.setItem('token',token);
  }

  public getHeaders() {
    return new HttpHeaders().set('Authorization', `Bearer ${this.getToken()}`);
  }
}
