import { Component, OnInit } from '@angular/core';
import { CookieService } from 'ngx-cookie-service';  // Assuming you're using ngx-cookie-service to handle cookies
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})

export class HomeComponent implements OnInit {

  username: string | null = null;

  constructor(private cookieService: CookieService, private router: Router) {
  }

  createCampaign() {
    const campaignName = (document.getElementById('campaignName') as HTMLInputElement).value;
    const campaignDescription = (document.getElementById('campaignDescription') as HTMLTextAreaElement).value;

    // You can handle the campaign creation logic here
    console.log('Campaign Name:', campaignName);
    console.log('Campaign Description:', campaignDescription);

    // Close the modal after creation. You may need to handle the modal closing in a more Angular-centric way, 
    // depending on your project setup, possibly with ViewChild and ElementRef for more control.
    $('#campaignModal').modal('hide');
  }

  ngOnInit() {
    this.username = this.cookieService.get('username') || null;
    if (this.username == null) {
      this.router.navigate(['/login']);
    }
  }

  navigateToLogin(): void {
    this.router.navigate(['/login']);
  }
}
<div class="container">
    <h1>User logged in: <span *ngIf="username; else noUser">{{ username }}</span></h1>
    <ng-template #noUser>No user logged in</ng-template>
    <button (click)="navigateToLogin()">Login</button>
</div>
