import { Component, OnInit } from '@angular/core';
import { CookieService } from 'ngx-cookie-service';  // Assuming you're using ngx-cookie-service to handle cookies
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})

export class HomeComponent implements OnInit {

  username: string | null = null;

  showModal: boolean = false;
  campaign = {
    campaignName: '',
    campaignDescription: '',
    username: ''
  };

  openModal() {
    this.showModal = true;
  }

  closeModal() {
    this.showModal = false;
  }

  submitCampaign() {
    // Implement submission logic here
    console.log('Campaign Submitted:', this.campaign);
    this.http.post('http://localhost:8080/private/add-campaign', this.campaign, { responseType: 'text' })
      .subscribe(response => {
        this.setCookie('username', this.username, 7);
        document.write("Logged in with " + this.username);
      }, error => {
        document.write("Error logging in");
      });
    this.closeModal();
  }

  constructor(private cookieService: CookieService, private router: Router, private http: HttpClient) {
  }



  ngOnInit() {
    this.username = this.cookieService.get('username') || null;
    if (this.username == null) {
      this.router.navigate(['/login']);
    }
  }

  navigateToLogin(): void {
    this.router.navigate(['/login']);
  }
}
