import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CampaignService } from '../campaign.service'; // Adjust the import path as needed

@Component({
  selector: 'app-campaign-view',
  templateUrl: './campaign-view.component.html',
  styleUrls: ['./campaign-view.component.css']
})
export class CampaignViewComponent implements OnInit {
  campaign: any;

  constructor(
    private campaignService: CampaignService,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      const campaignId = params['id']; // Get the id from the route parameter
      this.campaignService.getCampaign(campaignId).subscribe(
        (data) => {
          this.campaign = data;
        },
        (error) => {
          console.error('Error fetching campaign data:', error);
        }
      );
    });
  }
}
