import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-campaign-view',
  templateUrl: './campaign-view.component.html',
  styleUrls: ['./campaign-view.component.css'] // This will be your styles for the modern look
})
export class CampaignViewComponent implements OnInit {
  campaign: any;

  constructor(private campaignService: CampaignService) {}

  ngOnInit(): void {
    const campaignId = '1'; // Replace with the actual ID or a way to retrieve it (e.g., from route parameters)
    this.campaignService.getCampaign(campaignId).subscribe(
      (data) => {
        this.campaign = data;
      },
      (error) => {
        console.error('Error fetching campaign data:', error);
      }
    );
  }
}
